import {
    ADD_FAV
} from '../actions/types';

const inicialState = {
    user: null,
    itineraryId : null
};

export default function (state = inicialState, action) {
    console.log(action.payload)
    switch (action.type) {
        case ADD_FAV:
            //const userId = action.payload.user._id;
            //const itineraryId=action.payload.user.itinerariesFav
            return {
                ...state,
                user: action.payload
            };
                default:
                    return state;
    }
}
