import axios from 'axios';
import {
    ADD_FAV
} from '../actions/types';

export const addFav = (email, itineraryId) => dispatch => {
   
    // Request body


    axios.post ('http://localhost:8080/api/users/'+email+'/'+itineraryId)
    .then(res => dispatch ({
        type: ADD_FAV
    }))
    .catch(err => {
        dispatch(returnErrors(err.response.data, err.response.status, 'REGISTER_FAIL'));
        dispatch({
            type: REGISTER_FAIL
        });
    });
}
