import React, { Component } from 'react';
import { Form, FormGroup, Label, Input, FormText  } from 'reactstrap';
import { addComent } from '../actions/authActions';

class Coment extends Component{
    state = {
        coment : ''
      };


    onChange = e => {
        this.setState({ [e.target.name]: e.target.value });
      };


    onSubmit = e => {
        e.preventDefault();
    

        this.props.addComent(this.props.itineraryId,this.props.user._id,this.state.coment)
        
      };


    render(){

      

        return(
        <Form onSubmit={this.onSubmit}>
            <FormGroup>
                <Label for="exampleEmail">Coments</Label>
                <Input type='text'
                  name='coment'
                  id='coment'
                  placeholder='Add Coment for this itinerary !'
                  className='mb-3'
                  onChange={this.onChange}
                />
            </FormGroup>
        </Form>

        );
    }
}

const mapStateToProps = (state) => {
 
    return {
      itineraries: state.itinerario.itineraries,
      user: state.auth.user
    }
  }



export default Coment;
