import React, { Component } from 'react';
import { Button } from 'reactstrap';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { addFav } from '../actions/userActions';
import { loadUser } from '../actions/authActions';



class FavButton extends Component{
  constructor(props){
    super(props);
    this.state = {
      favoritos:[]
    }
  

  }

    static propTypes = {
        addFav: PropTypes.func.isRequired,
        loadUser: PropTypes.func.isRequired
      };

      componentDidMount(){

        this.props.loadUser()
        this.setState({
          favoritos : this.props.user.itinerariesFav
        })

      }
   

    agregarFav (){

        this.props.loadUser()
        this.setState({
          favoritos : this.props.user.itinerariesFav
        })

        const email = this.props.user.email;
        const itinerayId = this.props.itineraryId;
    
        console.log(this.props.user.itinerariesFav.length)

        if(!this.props.user.itinerariesFav.includes(itinerayId)){

          this.props.addFav(email, itinerayId);

        }else{
          console.log("ya fue incluido previamente")
        }

    };
    
showButtonFav(){
  
  const itinerayId = this.props.itineraryId;

  if(!this.state.favoritos.includes(itinerayId)){
    return <Button outline color="success" size="sm" onClick={this.agregarFav.bind(this)}>
            Add Favourite
        </Button>
  }else{
    return <Button size="sm" color="success" disabled>
            Favourite
        </Button>
  }
}

    render(){

      console.log(this.state.favoritos)

        return(
          this.showButtonFav()
        
        );
    }
}

// ItineraryList.propTypes = {
//     getItineraries: PropTypes.func.isRequired,
//     itineraries: PropTypes.object.isRequired
// }


const mapStateToProps = (state) => {
 
  return {
    user: state.auth.user
  }
}



export default connect(
    mapStateToProps,
    { addFav, loadUser })(FavButton);
