import {
    ADD_FAV
} from '../actions/types';

const inicialState = {
    user: null,
    itineraryFav:null
};

export default function (state = inicialState, action) {
    switch (action.type) {
        case ADD_FAV:
            return {
                ...state,
                user: action.payload._id,
                itineraryFav: action.payload.itineraryFav
            };
                default:
                    return state;
    }
}
