import {
    ADD_FAV,
    REMOVE_FAV
} from '../actions/types';

const inicialState = {
    user: null,
    itineraryId : []
};

export default function (state = inicialState, action) {
    const userId = action.payload._id;
            const itineraryId=action.payload.itinerariesFav
    switch (action.type) {
        case ADD_FAV:
            
            return {
                ...state,
                user: userId,
                itineraryId: itineraryId
            };
            case REMOVE_FAV:
            return {
                ...state,
                user: userId,
                itineraryId: itineraryId
            };
                default:
                    return state;
    }
}
