import React, { Component } from 'react';
import { ListGroup, ListGroupItem } from 'reactstrap';
import { connect } from 'react-redux';
import { getItems } from '../actions/itemActions';
import { getFilter } from '../actions/itemActions';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import ButtonBase from '@material-ui/core/ButtonBase';

import {Link} from "react-router-dom";



class CitiesList extends Component{
  
    constructor() {
        super()
        this.state = {
          filteredCities: [],
          cityFilter: "", 
        }
      }

    async componentDidMount() {
        
       await this.props.getItems();
       await this.setState({
        filteredCities: this.props.item.items
      })
       
       
    }


    handleChange = (e) => {
        this.setState({
            cityFilter: e.target.value
        })
        this.filterCities(this.state.cityFilter)
      }

      filterCities (e) {
        let filteredCities = this.props.item.items
        filteredCities = filteredCities.filter((city) => {
          let cityName = city.name.toLowerCase()
          return cityName.indexOf(
            e.target.value.toLowerCase()) !== -1
        })
        this.setState({
          filteredCities
        })
       this.props.getFilter(filteredCities)
      }

       
        
     


    render(){
      
      const classes = useStyles();
        return(
          
            <div>
            <div>
                <label htmlFor="filter">Filter by Cities: </label>
                <input type="text" id="filter" 
                
                onChange={this.filterCities.bind(this)}/>
            </div>


            {this.state.filteredCities?
            
        <ListGroup>
            {this.state.filteredCities.map((items) => (
            <ListGroupItem>

<div className={classes.root}>
      <Paper className={classes.paper}>
        <Grid container spacing={2}>
          <Grid item>
            <ButtonBase className={classes.image}>
              <img className={classes.img} alt="complex" src="/static/images/grid/complex.jpg" />
            </ButtonBase>
          </Grid>
          <Grid item xs={12} sm container>
            <Grid item xs container direction="column" spacing={2}>
              <Grid item xs>
                <Typography gutterBottom variant="subtitle1">
                  Standard license
                </Typography>
                <Typography variant="body2" gutterBottom>
                  Full resolution 1920x1080 â€¢ JPEG
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  ID: 1030114
                </Typography>
              </Grid>
              <Grid item>
                <Typography variant="body2" style={{ cursor: 'pointer' }}>
                  Remove
                </Typography>
              </Grid>
            </Grid>
            <Grid item>
              <Typography variant="subtitle1">$19.00</Typography>
            </Grid>
          </Grid>
        </Grid>
      </Paper>
    </div>

            <Link to={"/Cities/".concat(items._id)}> {items.name}</Link> 
            
            </ListGroupItem>
            ))}
        </ListGroup>:null
        }
        </div>
    
        );
    }
}

CitiesList.propTypes = {
    getItems: PropTypes.func.isRequired,
    item: PropTypes.object.isRequired
}


const mapStateToProps = (state) => ({
    item: state.item

})

export default connect(mapStateToProps, { getItems, getFilter })(CitiesList);
