import axios from 'axios';
import {
    ADD_FAV
} from '../actions/types';

export const addFav = (email, itineraryId) => dispatch => {
   
    // Request body


    axios.post ('http://localhost:8080/api/users/'+email+'/'+itineraryId)
    .then(res => dispatch (
        console.log(res.data),
        {
        
        type: ADD_FAV
    }))
    .catch(err => {
        console.log(err)
    });
}
