        </div>
        );
    }
}

const mapStateToProps = (state) => {
 
    return {
      user: state.auth.user,
      itinerarios : state.itinerario.itineraries,
      comments: state.comments
    }
  }



export default connect(
    mapStateToProps,
    { addComent, borrarComent, getComments }
  )(Coment);
